void TriggerHandler::handleCommLocalizationObjects_SlamParameter_SAVEMAP(const std::string &dirname, const std::string &filename)
{
	if(true == COMP->cartographerTask->FinishTrajectory(dirname, filename))
	{
		std::cout << "dir/file : " << dirname <<" / "<<filename << " .pgm"<<std::endl;
		bool is_map_saved_on_disk = COMP->cartographerTask->savemap(dirname, filename);
		if(is_map_saved_on_disk)
			std::cout << "MAP is saved on disk."<<std::endl;
		else
			std::cout << "MAP is not saved on disk."<<std::endl;
	}
}
