void IRClientUpcallManager::notify_upcalls(const CommBasicObjects::CommMobileIRScan &input)
{
	for(auto it=upcalls.begin(); it!=upcalls.end(); it++) {
		(*it)->on_IRClient(input);
	}
}

void IRClientUpcallManager::attach(IRClientUpcallInterface *upcall)
{
	upcalls.push_back(upcall);
}
void IRClientUpcallManager::detach(IRClientUpcallInterface *upcall)
{
	upcalls.remove(upcall);
}
