#include "GoalEventServerEventTestHandler.hh"

bool GoalEventServerEventTestHandler::testEvent(
	CommNavigationObjects::CommCdlGoalEventParameter &p,
	CommNavigationObjects::CommCdlGoalEventResult &r,
	const CommNavigationObjects::CdlGoalEventState &s
) throw() {
	bool result = false;

	CommNavigationObjects::CdlGoalEventType oldState, newState;

	p.get(oldState);
	s.get(newState);
	if ((oldState!=newState) && (newState==CommNavigationObjects::CdlGoalEventType::CDL_GOAL_REACHED))
	{
		p.set(newState);
		r.set(newState);
		std::cout<<"Event Testhandel true!"<<std::endl;
		result = true;
	}
	else
	{
		//  p.set(newState);
		result = false;
	}

	return result;
}
