RobotTask::RobotTask(SmartACE::SmartComponent *comp) 
:	RobotTaskCore(comp)
{
	std::cout << "constructor RobotTask\n";
	serial_fd = -1;

	this->robotPos.set_x(0.0);
	this->robotPos.set_y(0.0);
	this->robotPos.set_base_azimuth(0.0);

	this->rawPos.set_x(0.0);
	this->rawPos.set_y(0.0);
	this->rawPos.set_base_azimuth(0.0);

	updateV = false;
	updateOmega = false;

	this->oldPos.set_x(0.0);
	this->oldPos.set_y(0.0);
	this->oldPos.set_base_azimuth(0.0);

	oldth = 0;
	oldxpos = 0;
	oldypos = 0;
	totalDistance = 0;
	totalRotationLeft = 0;
	totalRotationRight = 0;

	// initialize covariance matrix
	this->oldPos.set_cov(0,0, 50*50);
	this->oldPos.set_cov(1,1, 50*50);
	this->oldPos.set_cov(2,2, 5*5/180.0*M_PI);
	this->robotPos.set_cov(0,0, oldPos.get_cov(0,0) );
	this->robotPos.set_cov(1,1, oldPos.get_cov(1,1) );
	this->robotPos.set_cov(2,2, oldPos.get_cov(2,2) );

	// uncertainity of robot
	lamdaSigmaD = 50*50/1000.0; // TODO
	lamdaSigmaDeltaAlpha = (5*5/360.0) /180.0 * M_PI; // TODO
	lamdaSigmaDeltaBeta = (2*2/1000.0) /180.0 * M_PI; // TODO
}
RobotTask::~RobotTask() 
{
	this->closeSerial();
	std::cout << "destructor RobotTask\n";
}

void RobotTask::setupRobotType(const int &robotType)
{
  // select the parameters according to the specified pioneer robot type
  switch(robotType)
  {
	// P3DX SH
	case ROBOT_TYPE_P3DX_SH:
	{
	   AngleConvFactor = 0.001534;
	   DiffConvFactor  = 0.0056;
	   DistConvFactor  = 1.0;
	   VelConvFactor   = 1.0;
	   RangeConvFactor = 1.0;
	   break;
	}

	// P3DX
	case ROBOT_TYPE_P3DX:
	{
	   AngleConvFactor = 0.001534;
	   DiffConvFactor  = 0.0056;
	   DistConvFactor  = 0.485;
	   VelConvFactor   = 1.0;
	   RangeConvFactor = 1.0;
	   break;
	}

	// P3AT SH
	case ROBOT_TYPE_P3AT_SH:
	{
	   AngleConvFactor = 0.001534;
	   DiffConvFactor  = 0.0034;
	   DistConvFactor  = 1.0;
	   VelConvFactor   = 1.0;
	   RangeConvFactor = 1.0;
	   break;
	}
  }
}

void RobotTask::on_NavVelIn(const CommBasicObjects::CommNavigationVelocity &input)
{
	// upcall triggered from InputPort NavVelIn
	// - use a local mutex here, because this upcal is called asynchroneously from outside of this task
	// - do not use longer blocking calls here since this upcall blocks the InputPort NavVelIn
	// - if you need to implement a long-running procedure, do so within the on_execute() method and in
	//   there, use the method navVelInGetUpdate(input) to get a copy of the input object

	int v = input.getVX();
	//FIXME: does it make sense to cast to int?
    int omega = (int)( input.get_omega()*180.0/M_PI);

    // set the robot's velocity
    //std::cout << "SetSpeed(" << v << ", " << omega << " )" << std::endl;
    this->setV( v );
    this->setOmegaDeg( omega );
}
